std::vector<std::vector<int>> LoadMap(std::string map_name, std::string layer_name) {
    std::string filepath = data_path() + "/maps/" + map_name + "/" + layer_name + ".dat";

    std::vector<std::vector<int>> output;
    int num;
    int width;
    int height;
    std::ifstream file(filepath);

    file >> width;
    file >> height;
    std::cout << "map width: " << width << std::endl;
    std::cout << "map height: " << height << std::endl;
    int index = 0;
    std::vector<int> arr;
    while (file >> num) {
        arr.push_back(num);
        index++;
        if (index >= width) {
            output.push_back(arr);
            arr.clear();
            index = 0;
        }
    }
    file.close();

    return output;
}